private void $STATES_PROC($STATE_TYPE $CURR_STATE, $COMMAND_TYPE $COMMAND_VAR)\n
{\n
	switch($CURR_STATE)\n
	{\n
%EASLList="StateCallBack" @separator="\n" @indent="\t\t\t" @owner=$StateMachineGUID @collection="AllStates"%
		default:\n
			break;\n
	}\n
