$COMMENT = "TransitionProc"
$sTransitionEffects = %EASLList="TransitionEffect" @indent="\t\t\t" @owner=$StateMachineGUID @collection="DerivedTransitions"%
private void $TRANSITION_PROC($TRANSITION_TYPE $TRANSITION_VAR)\n
