$COMMENT = "StateMachineInitialize"
$InitialState 		=  %EASL_GET("Property", $StateMachineGUID, "InitialState")%
$sInitialStateName 	=  %StateEnumeratedName($InitialState)%
private void $INITIALIZE_SM()\n
{ \n
	$CURR_STATE $EQ $STATE_TYPE.$sInitialStateName;\n
	$NEXT_STATE $EQ $CURR_STATE;\n
} \n

%PI="\n\t"%
\n
$COMMENT = "StateMachineRun"
private void $RUN_SM()
{
	$STATES_PROC($CURR_STATE, $COMMAND_TYPE.Entry);
	while(true)
	{
		$CURR_TRANSITION $EQ $TRANSITION_TYPE.$TRANSITION_NOTRANSITION;
		$STATES_PROC($CURR_STATE, $COMMAND_TYPE.Do);

		if ( $CURR_STATE != $NEXT_STATE)
		{
			$STATES_PROC($CURR_STATE, $COMMAND_TYPE.Exit);
			if ( $CURR_TRANSITION != $TRANSITION_TYPE.$TRANSITION_NOTRANSITION )
			{
				$TRANSITION_PROC( $CURR_TRANSITION );
			}
			if ( $NEXT_STATE == $STATE_TYPE.$STATES_NOSTATE )
			{
				break ; 
			}
			$STATES_PROC($NEXT_STATE, $COMMAND_TYPE.Entry);
			$CURR_STATE $EQ $NEXT_STATE ;
		}

		//Help to lower CPU Usage. You can change argument or remove following code
		try {
			Thread.currentThread().sleep(100);
		} catch (InterruptedException e) {
			e.printStackTrace();
		}
	}
}

