\nprivate void $sFuncName($COMMAND_TYPE $COMMAND_VAR)
{
	switch($COMMAND_VAR)
	{
		case Do:
		{
			if(!$STAY_IN_CURRENT_STATE_VAR)
			{
				// Do Behaviors..
%if $sParentDoBehaviors != "" %
				if ( $TRANSCEND_VAR )
				{
$sParentDoBehaviors
				}
%endIf%
$sDoBehaviors
			}			
			$STAY_IN_CURRENT_STATE_VAR $EQ true;

			// State's Transitions
$sTransitions
			break;
		}
%if $sEntryBehaviors != "" or $sParentEntryBehaviors!=""%
		case Entry:
		{
%if $sParentEntryBehaviors != "" %
			if ( $TRANSCEND_VAR )
			{
$sParentEntryBehaviors
			}
%endIf%
			//Entry Behaviors..
$sEntryBehaviors
			break;
		}
%endIf%
%if $sExitBehaviors != "" or $sParentExitBehaviors!=""%
		case Exit:
		{
%if $sParentExitBehaviors != "" %
			if ( $TRANSCEND_VAR )
			{
$sParentExitBehaviors
			}
%endIf%
			//Exit Behaviors..
$sExitBehaviors
			break;
		}
%endIf%
		default:
		{
			break;
		}
	}
}

